import Todo from "./Todo";
import styled from "styled-components";
import { useState } from "react";

var todos = [
  { Task: "Gør rent", Done: false, Due: "20.10.2020" },
  { Task: "Køb ind", Done: true, Due: "30.10.2020" },
  { Task: "Vask tøj", Done: false, Due: "1.11.2020" },
];

var Container = styled.div`
  text-align: center;
`;

var TodoListContainer = styled.div`
  width: 400px;
  margin: auto;
  background-color: #dadae0;
  border-radius: 5px;
  margin-bottom: 10px;
  padding-bottom: 10px;
`;

var TodoTop = styled.h4`
  border: 1px solid black;
  padding: 20px;
`;

function todosLeft(todos) {
  var count = 0;
  for (var i = 0; i < todos.length; i++) {
    if (todos[i].Done == false) {
      count = count + 1;
    }
  }
  return count;
}

function markDone(ts, i) {
  console.log("HERe");
  ts[i].Done = !ts[i].Done;
  console.log(ts);
  return [...ts];
}

function App() {
  var [newTodos, setTodos] = useState(todos);
  return (
    <Container>
      <h1>To do list</h1>
      <TodoListContainer>
        <TodoTop>{todosLeft(todos)} opgaver tilbage</TodoTop>
        <ul>
          {newTodos
            .sort((a, b) => (a.Due < b.Due ? 1 : -1))
            .sort((a, b) => (a.Done ? 1 : -1))
            .map((t, i) => (
              <Todo
                Task={t.Task}
                Done={t.Done}
                Due={t.Due}
                key={i}
                onClick={() => setTodos(markDone(newTodos, i))}
              />
            ))}
        </ul>
      </TodoListContainer>
    </Container>
  );
}

export default App;
