export async function getTodos() {
  var id = await getId("https://study-now-tody.herokuapp.com/?id=" + id);
  var response = await fetch("https://study-now-tody.herokuapp.com/?id=" + id);
  return [
    { Task: "Gør rent", Done: false, Due: "20.10.2020" },
    { Task: "Køb ind", Done: true, Due: "30.10.2020" },
    { Task: "Vask tøj", Done: false, Due: "1.11.2020" },
  ];
}

export function markDone(todos, i) {
  todos[i].Done = !todos[i].Done;
  return [...todos];
}

export function createTodo(task, due) {
  return {
    Task: task,
    Due: due,
    Done: false,
  };
}

async function getId() {
  var id = localStorage("todo-app-id");
  if (id === undefined) {
    var response = await fetch(
      "https://study-now-tody.herokuapp.com/id"
    ).json();
    id = response.id;
    localStorage.setItem("todo-app-id", id);
  }
  return id;
}
